package marchenkov.ivan;import java.util.Scanner;public class Utils {    public static int scanIntValue(int min, int max) {        String next = null;        do {            try {                next = new Scanner(System.in).next();                int value =  Integer.parseInt(next);                if (value >= min && value <= max) {                    return value;                } else {                    System.out.print(String.format("Error!!! Value will be in range %d, %d!\r\n", min, max));                    System.out.print("Enter value again: ");                }            } catch (NumberFormatException e) {                if (next.equals("q")) System.exit(0);                System.out.print("Error!!! Type of value is not integer!");                System.out.print("Enter value again: ");            }        } while (true);    }}