package controller;import java.io.Serializable;public class ListRequest implements Serializable {    private static final long serialVersionUID = -744044292946427607L;    private int page;    private int size;    private String orders;    public int getPage() {        return page;    }    public void setPage(int page) {        this.page = page;    }    public int getSize() {        return size;    }    public void setSize(int size) {        this.size = size;    }    public String getOrders() {        return orders;    }    public void setOrders(String orders) {        this.orders = orders;    }}