package controller;import java.io.Serializable;import java.util.List;public class TableResponse implements Serializable {    private static final long serialVersionUID = 5739237841371939495L;    private int page = 0;    private int size = 10;    private List list;    public int getPage() {        return page;    }    public void setPage(int page) {        this.page = page;    }    public int getSize() {        return size;    }    public void setSize(int size) {        this.size = size;    }    public List getList() {        return list;    }    public void setList(List list) {        this.list = list;    }}