package db;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class ConnectionPool {    private static Connection connection;    public static Connection getConnection() {        try {            Class.forName("org.postgresql.Driver");        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        try {            connection = DriverManager.getConnection("database.url", "database.user", "database.password");        } catch (SQLException e) {            e.printStackTrace();        }        return connection;    }}