package services.baseQuery;import java.util.List;public class QueryBuilder {    private StringBuilder query = new StringBuilder();    public QueryBuilder select(List<String> selections) {        query.append("SELECT ");        return this;    }    public QueryBuilder from(Class entityClass) {        query.append("FROM ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder insert(Class entityClass) {        query.append("INSERT INTO ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder update(Class entityClass) {        query.append("UPDATE ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder delete(Class entityClass) {        query.append("DELETE FROM ");        tableNamePart(entityClass);        return this;    }    private void tableNamePart(Class entityClass) {        query.append(entityClass.getSimpleName()).                append(" ");    }}