package servlets;import controller.ChiefController;import controller.Controller;import controller.ListRequest;import controller.TableResponse;import view.View;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;@WebServlet(urlPatterns = "/chief/list")public class CarListServlet extends HttpServlet {    private final Controller controller = new ChiefController();    private final View view = new View();    @Override    protected void doPost(HttpServletRequest request, HttpServletResponse response) {       TableResponse tableResponse = controller.getList((ListRequest) request.getAttribute("request"));       view.setAttribute(request, "response", tableResponse);       view.forward(request, response, "chiefList.jsp");    }}