package view;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;public class View {    public void setAttribute(HttpServletRequest request, String key, Object data) {        request.setAttribute(key, data);    }    public void forward(HttpServletRequest request, HttpServletResponse response, String path) {        RequestDispatcher requestDispatcher = request.getRequestDispatcher(path);        try {            requestDispatcher.forward(request, response);        } catch (ServletException | IOException e) {            e.printStackTrace();        }    }    public void redirect(HttpServletRequest request, HttpServletResponse response, String path) {        try {            response.sendRedirect(request.getContextPath() + path);        } catch (IOException e) {            e.printStackTrace();        }    }}